(function main(){

    'use strict';
    
    var exec = require('child_process').exec;

    console.log('herokuNoSleep: start.');
    
    setInterval(function(){
        
        exec('curl https://acim7.herokuapp.com', function (error, stdout, stderr) {
            if(stdout){
                console.log('herokuNoSleep: success.');
            }
            if(error){
                console.log('herokuNoSleep: failure.');
            }
        });
    }, (1000 * 60 * 5));
    
})();
